# creating a class for a one slot/element of a fieldclass Element:    def __init__(self, x, y, index) -> None:        self.x = x        self.y = y        self.index = index        self.value = None    def __eq__(self, other) -> bool:        return self.x == other.x and self.y == other.y and self.index == other.index    def update_value(self, value) -> None:            if self.value is None:                self.value = value    def get_value(self):        return self.value    def get_index(self) -> int:        return self.index    def get_coordinates(self) -> tuple:        return self.x, self.y    def __str__(self):        return f'Element with ({self.x}, {self.y}) cord. and value is {self.value} and index {self.index} '    def __repr__(self):        return f'Element(x={self.x}, y={self.y}, index = {self.index}, value={self.value})'