import pygamefrom constants import *class Infrastructure:    def __init__(self):        pygame.init()        self.screen = pygame.display.set_mode([WIDTH, HEIGHT])        self.clock = pygame.time.Clock()        self.font = pygame.font.Font(None, SCALE)    def is_quit_event(self) -> bool:        for event in pygame.event.get():            if event.type == pygame.QUIT:                return True        return False    def fill_screen(self):        self.screen.fill(SCREEN_COLOR)    def draw_circle(self, ):        pass    def draw_cross(self, ):        pass    def draw_game_over(self, winner):        message = self.font.render(f'{winner}', True, pygame.Color('red'))        self.screen.blit(            message,            message.get_rect(center=(WIDTH // 2, HEIGHT // 2))        )    def update_and_tick(self):        pygame.display.update()        self.clock.tick(FPS)    def quit(self):        pygame.quit()