# class which describes one local game logicfrom src.element import Elementfrom src.constants import *from src.utilit import find_center_coordinatesclass LocalGame:    def __init__(self, field_center):        self.slots_list = [Element(*find_center_coordinates(i, field_center, SCALE), i) for i in range(9)]        self.game_status = None    def __getitem__(self, item):        return  self.slots_list[item]    def check_winner(self, value):        all_elem_indexes = [i.get_index() for i in self.slots_list if i.get_value() == value]        for win_comb in WINNING_COMBINATIONS:            if win_comb[0] in all_elem_indexes and win_comb[1] in all_elem_indexes and win_comb[2] in all_elem_indexes:                self.game_status = value                return None    def check_draw(self):        if self.game_status is None and len([i for i in self.slots_list if i.get_value() is None]) == 0:            # means  a draw            self.game_status = 0    def one_move(self, index:int, value:int) -> None:        if self.slots_list[index].get_value() is None:            self.slots_list[index].update_value(value)            self.check_winner(value)            self.check_draw()            return 1        else:            return 0