# writing helping functions# calculating center coordinates of each local game by its indexdef find_center_coordinates(index:int, field_center:tuple, SCALE:int, k:int = 1) -> tuple:    if index == 0:        return field_center[0] - k * SCALE, field_center[1] + k * SCALE    elif index == 1:        return field_center[0], field_center[1] + k * SCALE    elif index == 2:        return field_center[0] + k * SCALE, field_center[1] + k * SCALE    elif index == 3:        return field_center[0] - k * SCALE, field_center[1]    elif index == 4:        return  field_center    elif index == 5:        return field_center[0] + k * SCALE, field_center[1]    elif index == 6:        return field_center[0] - k * SCALE, field_center[1] - k * SCALE    elif index == 7:        return field_center[0], field_center[1] - k * SCALE    elif index == 8:        return field_center[0] + k * SCALE, field_center[1] - k * SCALE